sysauto_script.condition:
var answer = true;
var rightNow = new GlideDateTime();
var year = rightNow.getYearLocalTime();
var month = rightNow.getMonthLocalTime();
var day = rightNow.getDayOfMonthLocalTime();
var dayOfWeek = rightNow.getDayOfWeekLocalTime();
if(dayOfWeek == 6 || dayOfWeek == 7){
	answer = false;
}

answer;

sysauto_script.script:
var notClockIn = new GlideRecord('x_727664_kaoqin_attendance_info');
notClockIn.addQuery('date','=',year+'-'+month+'-'+day);
notClockIn.addQuery('is_ferver','=','');

notClockIn.query();

while(notClockIn.next()){
  gs.info("The person who didn't clock in today  = " + notClockIn.usernm);
  gs.eventQueue('x_727664_kaoqin.didNotClockInToday',notClockIn,notClockIn.usernm,gs.getUserName());
}
